package revision;import java.util.Arrays;public class CyclicSort {    public static void main(String args[]){        int[] array = {4,2,5,1,3};        cyclic(array);        System.out.println("Sorted -> " + Arrays.toString(array));    }    static void cyclic(int[] arr){        int i = 0;        while(i < arr.length){            if(i != arr[i]-1){                int temp = arr[i];                arr[i] = arr[temp-1];                arr[temp-1] = temp;            }else{                i++;            }        }    }}