package revision;import java.util.ArrayList;import java.util.List;public class FindAllMissingElement {    public static void main(String args[]){        int[] array = {4,3,2,7,8,2,3,1};        List<Integer> ls = findMissingValue(array);        System.out.println(ls.toString());    }    static List<Integer> findMissingValue(int[] array){        int i = 0;        List<Integer> result = new ArrayList<>();        while(i < array.length){            int correct = array[i]-1;            if(array[i] != array[correct]){                int temp = array[i];                array[i] = array[correct];                array[correct] = temp;            }else{                i++;            }        }        for(int index = 0;index < array.length;index++){            if(index+1 != array[index]){                result.add(index+1);            }        }        return result;    }}