package revision;public class FindOccurences {    public static void main(String[] args){        int[] array = {0,1,2,3,3,3,4,4,5};        System.out.println(findOccurences(0,array.length-1,3,array,-1));    }    static int findOccurences(int start, int end, int target, int[] array, int occuresAt) {        if(start > end){            return occuresAt;        }        int mid = start + (end - start) / 2;        if(target > array[mid]){            return findOccurences(mid+1,end,target,array,occuresAt);        } else if(target < array[mid]){            return findOccurences(start,mid-1,target,array,occuresAt);        }else{            occuresAt = mid;            return findOccurences(start,mid-1,target,array,occuresAt);        }    }}