package org.example;public class Polymorphism {    public Polymorphism() {        System.out.println("CAlled Parent");    }    public static void getName(){        System.out.println("Parent !");    }    public void exceptionTest() throws ArrayIndexOutOfBoundsException{        throw new ArithmeticException();    }}class ChildPolymorphism extends Polymorphism{    public ChildPolymorphism() {        System.out.println("Called child polymorphism");    }    public static void main(String args[]){        Polymorphism p = new ChildPolymorphism();        p.exceptionTest();        p.getName();    }}