package org.example;import java.util.ArrayList;import java.util.Arrays;import java.util.List;public class QuickSort {    public static void main(String args[]){        int[] array = {2,4,1,5,3,6};        List<Integer> arrayList = Arrays.asList(2,4,1,5,3,6);        System.out.println(quickSort(arrayList).toString());    }    static List<Integer> quickSort(List<Integer> arrayList){        if(arrayList.size() < 2){            return arrayList;        }        int pivot = arrayList.get(0);        List<Integer> greater = new ArrayList<>();        List<Integer> lesser = new ArrayList<>();        for(Integer i : arrayList){            if(pivot == i){                continue;            }            if(pivot < i){                greater.add(i);            }else{                lesser.add(i);            }        }        quickSort(lesser).add(pivot);        lesser.addAll(quickSort(greater));        return lesser;    }}