package org.example.linkedList;public class FindMidlleLinkedList {    public ListNode middleNode(ListNode head) {        ListNode fast = head;        ListNode slow = head;        while(fast != null && fast.next != null){            fast = fast.next.next;            slow = slow.next;        }        return slow;    }    private class ListNode {        int val;        ListNode next;        ListNode(int x) {            val = x;            next = null;        }    }}