package org.example.linkedList;public class HappyNumber {    public boolean isHappy(int n) {        int fast = n;        int slow = n;        do{            fast = findSquare(findSquare(fast));            slow = findSquare(slow);        }while(fast != slow);        if(slow == 1){            return true;        }        return false;    }    static int findSquare(int number){        int ans = 0;        while(number > 0){            int rem = number % 10;            ans += rem * rem;            number /= 10;        }        return ans;    }    private class ListNode {        int val;        ListNode next;        ListNode(int x) {            val = x;            next = null;        }    }}