package org.example.linkedList;//https://leetcode.com/problems/reverse-linked-list/description/public class ReversalOfLinkedList {    public static void main(String args[]){    }    public ListNode inPlaceReversal(ListNode node){        if(node == null){            return node;        }        ListNode present = node;        ListNode next = node.next;        ListNode previous = null;        while(present != null){            present.next = previous;            previous = present;            present = next;            if(next != null){                next = next.next;            }        }        return previous;    }    private class ListNode {      int val;      ListNode next;      ListNode() {}      ListNode(int val) { this.val = val; }      ListNode(int val, ListNode next) { this.val = val; this.next = next; }  }}