package org.example.multithreading;public class CounterInMultithreading {    public static void main(String args[]){        Counter counter = new Counter();        ThreadIncreament[] threads = new ThreadIncreament[5];        for(int i = 0;i < 5; i++){            threads[i] = new ThreadIncreament(counter,2);            threads[i].start();        }        try{            for(ThreadIncreament thread : threads){                thread.join();            }        }catch (InterruptedException exception){            System.out.println(exception);        }        System.out.println("Count -> "+ counter.getCount());    }}