package org.example.revised;import java.util.Arrays;public class BubbleSort {    public static void main(String args[]){        int[] array = {1,4,5,2,3};        bubble(array);        System.out.println("Sorted array -> "+ Arrays.toString(array));    }    static void bubble(int[] array){        boolean isSwapped;        for(int i = 0;i<array.length;i++){            isSwapped = false;            for(int j = 1; j<array.length-i;j++){                if(array[j] < array[j-1]){                    int temp = array[j];                    array[j] = array[j-1];                    array[j-1] = temp;                    isSwapped = true;                }            }            if(!isSwapped){                break;            }        }    }}