package org.example.revised;public class FindFirstAndLastOccurence {    public static void main(String args[]){        int[] array = {1,2,2,2,2,3,4,5,6};        System.out.println("First occurence - "+ findOccurence(2,array,true));        System.out.println("Second occurence - "+ findOccurence(2,array,false));    }    static int findOccurence(int target, int[] array,boolean searchFirstIndex){        int start = 0;        int end = array.length-1;        int mid = 0;        int ans = 0;        while(start <= end){            mid = start + (end-start)/2;            if(target > array[mid]){                start = mid + 1;            }else if(target < array[mid]){                end = mid - 1;            }else{                ans = mid;                if(searchFirstIndex){                    end = mid - 1;                }else{                    start = mid + 1;                }            }        }        return ans;    }}