package org.example.revised;public class OddEvenBitwise {    public static void main(String args[]){        System.out.println("Is odd -> "+ isOdd(2));        int[] array = {2,3,4,1,2,1,3,6,4};        System.out.println("Unique -> "+ findUniqueDigit(array));    }    static boolean isOdd(int num){        return ((num&1) == 1);    }    static int findUniqueDigit(int[] array){        int result = 0;        for(int a : array){            result ^= a;        }        return result;    }}