package org.example.revised;public class OrderAgnosticBinarySearch {    public static void main(String args[]){        int[] array = {1,2,3,4,5,6,7,8};        System.out.println("Target at -> " + findElement(0, array.length - 1,array, 5, (array[0] < array[array.length-1])));    }    static int findElement(int start, int end, int[] array, int target,boolean isAsc){        if(start > end){            return -1;        }        int mid = start + (end - start)/2;        if(isAsc){            if(target > array[mid]){                return findElement(mid + 1, end, array, target, isAsc);            }else if(target < array[mid]){                return findElement(start, mid - 1, array, target, isAsc);            }        }else{            if(target > array[mid]){                return findElement(start, mid -1, array, target, isAsc);            }else if (target < array[mid]){                return findElement(mid + 1, end, array, target, isAsc);            }        }        return mid;    }}